package p2021_06_17;

public class Ooer11 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//증감 연산자 : ++, ==
		int a=10, b=10;
		
		System.out.println("a=:"+a++);
		System.out.println("a=:"+a);
		
		System.out.println("b=:"+(++b));
		System.out.println("a=:"+b);
	}

}
