package p2021_06_17;

public class Oper09 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

//		확장 대입 연산자
//		+=, -=, *=, /=, %=
		int a = 10, b = 3;
		System.out.println(a+=b);
		System.out.println(a-=b);
		System.out.println(a*=b);
		System.out.println(a/=b);
		System.out.println(a%=b);

	}

}
